import React, { useState } from 'react';
import { ShowPasswordProps } from './type';

export const ShowPassword = ({ value, onChange }: ShowPasswordProps) => {
  const [showPassword, setShowPassword] = useState(false);

  const handleChange = () => {
    setShowPassword(!showPassword);
    onChange(showPassword);
  };

  return (
    <div className="flex w-full items-center justify-start gap-2">
      <input
        name="display-password"
        id="display-password"
        type="checkbox"
        checked={value}
        onChange={handleChange}
        className="peer relative left-0 h-5 w-5 cursor-pointer appearance-none rounded-sm border border-skin-base outline-none after:absolute after:left-0 after:top-0 after:h-full after:w-full after:bg-[length:40px] after:bg-center after:bg-no-repeat after:content-[''] checked:bg-skin-base after:checked:bg-[url('data:image/svg+xml;base64,')]"
      />
      <label
        htmlFor="display-password"
        className="inline-block cursor-pointer text-lg font-medium"
      >
        Show password
      </label>
    </div>
  );
};
